// 用例测试#include <iostream>#include <vector>using namespace std;#include "DoMergeTwoListsSolution.cpp"ListNode* createList(std::initializer_list<int> elements);void printList(ListNode* head);// 测试用例 g++ -std=c++11 main.cpp -o DoMergeTwoListsSolutionint main(){    // 创建两个有序链表    ListNode* l1 = createList({1, 2, 4});    ListNode* l2 = createList({1, 3, 4});    // 合并链表//    printList(l1);//    printList(l2);    DoMergeTwoListsSolution solution;    ListNode* head = solution.mergeTwoLists(l1, l2);    printList(head);    return 0;}// 打印合并后的链表void printList(ListNode* head) {    while (head != nullptr) {        std::cout << head->val << " ";        head = head->next;    }    std::cout << std::endl;}ListNode* createList(std::initializer_list<int> elements) {    ListNode* dummy = new ListNode(0);    ListNode* current = dummy;    for (int val : elements) {        current->next = new ListNode(val);        current = current->next;    }    ListNode* head = dummy->next;    delete dummy;    return head;}